    private void ProcesoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ProcesoActionPerformed
                double Band1,Band2,Band3,resultado;
        Band1=Double.parseDouble(Banda1.getText());
        Band2=Double.parseDouble(Banda2.getText());
        Band3=Double.parseDouble(Banda3.getText());

        //condicional anidado, que busca hallar equivalencias de la resistencia
        if(Band3 <= 0||Band1 <= 0)
        {
            Banda1.setText("");
            Banda2.setText("");
            Banda3.setText("");
            Resp.setText("Error");
        }
        if (Band2 == 0)
        {
            if(Band3 == 1)
            {
                resultado = Band1*10*10;
                Resp.setText(String.valueOf(resultado));
            }
            else
            {
                if(Band3 == 2)
                {
                    resultado = Band1*10*100;
                    Resp.setText(String.valueOf(resultado));
                }
                else
                {
                    if(Band3 == 3)
                    {
                        resultado = Band1*10*1000;
                        Resp.setText(String.valueOf(resultado));
                    }
                    else
                    {
                        if(Band3 == 4)
                        {
                            resultado = Band1*10*10000;
                            Resp.setText(String.valueOf(resultado));
                        }
                        else
                        {
                            if(Band3 == 5)
                            {
                                resultado = Band1*10*100000;
                                Resp.setText(String.valueOf(resultado));
                            }
                            else
                            {
                                if(Band3 == 6)
                                {
                                    resultado = Band1*10*1000000;
                                    Resp.setText(String.valueOf(resultado));
                                }
                            }
                        }
                    }
                }

            }
        }
        else
        {
            if(Band3 == 1)
            {
                resultado = (Band1*10)+Band2;
                Resp.setText(String.valueOf(resultado));
                System.out.print(resultado);
            }else
            {
                if(Band3 == 2)
                {
                    resultado = ((Band1*10)+Band2)*100;
                    Resp.setText(String.valueOf(resultado));
                    System.out.print(resultado);
                }
                else
                {
                    if(Band3 == 3)
                    {
                        resultado = ((Band1*10)+Band2)*1000;
                        Resp.setText(String.valueOf(resultado));
                    }
                    else
                    {
                        if(Band3 == 4)
                        {
                            resultado = ((Band1*10)+Band2)*10000;
                            Resp.setText(String.valueOf(resultado));
                        }
                        else
                        {
                            if(Band3 == 5)
                            {
                                resultado = ((Band1*10)+Band2)*100000;
                                Resp.setText(String.valueOf(resultado));
                            }
                            else
                            {
                                if(Band3 == 6)
                                {
                                    resultado = ((Band1*10)+Band2)*1000000;
                                    Resp.setText(String.valueOf(resultado));
                                }
                                else
                                {
                                    if(Band3 == 7)
                                    {
                                        resultado = ((Band1*10)+Band2)*10000000;
                                        Resp.setText(String.valueOf(resultado));
                                    }
                                    else
                                    {
                                        if(Band3 == 8)
                                        {
                                            resultado = ((Band1*10)+Band2)*100000000;
                                            Resp.setText(String.valueOf(resultado));
                                        }
                                        else
                                        {
                                            if(Band3 == 9)
                                            {
                                                resultado = ((Band1*10)+Band2)*1000000000;
                                                Resp.setText(String.valueOf(resultado));
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }

            }
        }
    }//GEN-LAST:event_ProcesoActionPerformed

